create table contato (id int8 generated by default as identity, cidade varchar(255), email varchar(255), mensagem varchar(255), nome varchar(255), pais varchar(255), sobrenome varchar(255), telefone varchar(255), primary key (id));
create table ong (id int8 generated by default as identity, fburl varchar(255), instaurl varchar(255), logourl varchar(255), mapaurl varchar(255), sobre varchar(255), primary key (id));
